import { createContext, useContext, useState, useEffect, ReactNode, useCallback } from 'react';
import { User, AuthContextType } from '../types';
import { apiClient } from '../services/api';

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const useAuth = (): AuthContextType => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

interface AuthProviderProps {
  children: ReactNode;
}

export const AuthProvider = ({ children }: AuthProviderProps) => {
  const [user, setUser] = useState<User | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Handle auth failure (when refresh fails)
  const handleAuthFailure = useCallback(() => {
    setUser(null);
    setIsAuthenticated(false);
  }, []);

  // Check authentication status on mount and when needed
  const checkAuth = useCallback(async () => {
    try {
      // Try to get current user info
      const userData = await apiClient.getMe();

      // User is authenticated
      setUser({
        id: userData.id,
        email: userData.email,
        created_at: new Date().toISOString(), // API doesn't return this, use current time
      });
      setIsAuthenticated(true);
      return true;
    } catch (error: any) {
      // If 401, try to refresh (this is expected when user is not logged in)
      if (error.response?.status === 401) {
        try {
          // Attempt to refresh the token
          await apiClient.refresh();

          // If refresh succeeds, try getting user info again
          try {
            const userData = await apiClient.getMe();
            setUser({
              id: userData.id,
              email: userData.email,
              created_at: new Date().toISOString(),
            });
            setIsAuthenticated(true);
            return true;
          } catch (meError: any) {
            // Refresh worked but /auth/me still failed - shouldn't happen but handle it
            console.warn('Refresh succeeded but /auth/me failed:', meError.response?.status);
            setUser(null);
            setIsAuthenticated(false);
            return false;
          }
        } catch (refreshError: any) {
          // Refresh failed - user is not logged in (expected if no cookies exist)
          // Don't log this as an error, it's normal when user hasn't logged in
          if (refreshError.response?.status !== 401) {
            // Only log if it's not a 401 (which is expected when not logged in)
            console.error('Unexpected refresh error:', refreshError);
          }
          setUser(null);
          setIsAuthenticated(false);
          return false;
        }
      }

      // Other errors (network, etc.)
      console.error('Unexpected error checking auth:', error);
      setUser(null);
      setIsAuthenticated(false);
      return false;
    }
  }, []);

  // Check auth status on mount
  useEffect(() => {
    checkAuth().finally(() => {
      setIsLoading(false);
    });
  }, [checkAuth]);

  // Set auth failure callback on API client
  useEffect(() => {
    apiClient.setOnAuthFailure(handleAuthFailure);
  }, [handleAuthFailure]);

  const login = async (email: string, password: string): Promise<void> => {
    try {
      // Sign in - cookies are set by backend
      await apiClient.signIn({ email, password });

      // Wait a brief moment for cookies to be set by the browser
      // Then get user info after successful login
      await new Promise(resolve => setTimeout(resolve, 100));

      // Retry logic for getting user info (cookies might need a moment)
      let retries = 3;
      let userData: AuthMeResponse | null = null;

      while (retries > 0 && !userData) {
        try {
          userData = await apiClient.getMe();
        } catch (error: any) {
          retries--;
          if (retries > 0) {
            // Wait a bit longer before retrying
            await new Promise(resolve => setTimeout(resolve, 200));
          } else {
            // If all retries fail, use email from login form as fallback
            console.warn('Could not fetch user info after login, using email from form');
            setUser({
              id: 0, // Temporary ID, will be updated on next checkAuth
              email: email.trim(),
              created_at: new Date().toISOString(),
            });
            setIsAuthenticated(true);
            // Trigger a checkAuth in the background to get proper user data
            setTimeout(() => checkAuth(), 1000);
            return;
          }
        }
      }

      if (userData) {
        setUser({
          id: userData.id,
          email: userData.email,
          created_at: new Date().toISOString(),
        });
        setIsAuthenticated(true);
      }
    } catch (error) {
      console.error('Login failed:', error);
      setUser(null);
      setIsAuthenticated(false);
      throw error;
    }
  };

  const logout = useCallback(async (): Promise<void> => {
    try {
      // Optionally call logout endpoint if backend has one
      // await apiClient.logout();
    } catch (error) {
      console.error('Logout error:', error);
    } finally {
      // Clear local state regardless
      setUser(null);
      setIsAuthenticated(false);
    }
  }, []);

  const value: AuthContextType = {
    user,
    token: user !== null ? 'cookie' : null, // Keep token for compatibility, using cookie authentication
    login,
    logout,
    isLoading,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
