import React from 'react';
import { AlertCircle, Wifi, WifiOff } from 'lucide-react';

interface MockApiNotificationProps {
  isUsingMockApi: boolean;
}

export const MockApiNotification: React.FC<MockApiNotificationProps> = ({ isUsingMockApi }) => {
  if (!isUsingMockApi) return null;

  return (
    <div className="bg-yellow-50 border-l-4 border-yellow-400 p-4 mb-4">
      <div className="flex items-center">
        <div className="flex-shrink-0">
          <WifiOff className="h-5 w-5 text-yellow-400" />
        </div>
        <div className="ml-3">
          <p className="text-sm text-yellow-700">
            <strong>Demo Mode:</strong> Backend API is not available. Using mock data for demonstration.
            <br />
            <span className="text-xs">
              To connect to a real backend, make sure your Vocatree API is running on port 8000.
            </span>
          </p>
        </div>
      </div>
    </div>
  );
};
