import React, { useState, useRef, useEffect } from 'react';
import { 
  MoreVertical, 
  Edit2, 
  Trash2, 
  Plus, 
  Move, 
  Copy,
  Folder,
  FileText,
  Sparkles,
  BookOpen
} from 'lucide-react';

export interface DropdownMenuItem {
  id: string;
  label: string;
  icon: React.ReactNode;
  onClick: () => void;
  disabled?: boolean;
  destructive?: boolean;
}

interface DropdownMenuProps {
  items: DropdownMenuItem[];
  className?: string;
  disabled?: boolean;
}

export const DropdownMenu: React.FC<DropdownMenuProps> = ({
  items,
  className = '',
  disabled = false,
}) => {
  const [isOpen, setIsOpen] = useState(false);
  const [position, setPosition] = useState({ top: 0, right: 0, openUpward: false });
  const [isMobile, setIsMobile] = useState(false);
  const buttonRef = useRef<HTMLButtonElement>(null);
  const menuRef = useRef<HTMLDivElement>(null);

  // Check if mobile
  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth < 768);
    };
    checkMobile();
    window.addEventListener('resize', checkMobile);
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  // Calculate menu position with proper viewport detection
  const updatePosition = () => {
    if (buttonRef.current && menuRef.current) {
      const rect = buttonRef.current.getBoundingClientRect();
      const menuRect = menuRef.current.getBoundingClientRect();
      const viewportWidth = window.innerWidth;
      const viewportHeight = window.innerHeight;
      const scrollY = window.scrollY;
      const scrollX = window.scrollX;

      // Get actual or estimated menu height
      const menuHeight = menuRect.height || Math.min(items.length * 44 + 8, viewportHeight * 0.6);
      const menuWidth = menuRect.width || 240;
      const gap = 4;
      const padding = 8; // Viewport padding

      // Calculate available space
      const spaceBelow = viewportHeight - rect.bottom;
      const spaceAbove = rect.top;
      const spaceRight = viewportWidth - rect.right;
      const spaceLeft = rect.left;

      // Determine if menu should open upward
      const shouldOpenUpward = spaceBelow < menuHeight + gap && spaceAbove > spaceBelow;

      // Calculate vertical position
      let top: number;
      if (shouldOpenUpward) {
        top = rect.top + scrollY - menuHeight - gap;
      } else {
        top = rect.bottom + scrollY + gap;
      }

      // Ensure menu stays within viewport bounds
      const maxTop = scrollY + padding;
      const minTop = scrollY + viewportHeight - menuHeight - padding;
      top = Math.max(maxTop, Math.min(minTop, top));

      // Calculate horizontal position (align to right edge of button)
      let right = viewportWidth - rect.right - scrollX;

      // If not enough space on right, align to left edge
      if (right < menuWidth + padding && spaceLeft > spaceRight) {
        right = viewportWidth - rect.left - scrollX;
      }

      // Ensure menu doesn't go off screen horizontally
      right = Math.max(padding, Math.min(right, viewportWidth - menuWidth - padding));

      setPosition({ 
        top, 
        right, 
        openUpward: shouldOpenUpward 
      });
    } else if (buttonRef.current) {
      // Fallback if menu not yet rendered - use estimated height
      const rect = buttonRef.current.getBoundingClientRect();
      const viewportWidth = window.innerWidth;
      const viewportHeight = window.innerHeight;
      const scrollY = window.scrollY;
      const scrollX = window.scrollX;

      const estimatedMenuHeight = Math.min(items.length * 44 + 8, viewportHeight * 0.6);
      const menuWidth = 240;
      const gap = 4;
      const padding = 8;

      const spaceBelow = viewportHeight - rect.bottom;
      const spaceAbove = rect.top;
      const spaceRight = viewportWidth - rect.right;
      const spaceLeft = rect.left;

      const shouldOpenUpward = spaceBelow < estimatedMenuHeight + gap && spaceAbove > spaceBelow;

      let top: number;
      if (shouldOpenUpward) {
        top = rect.top + scrollY - estimatedMenuHeight - gap;
      } else {
        top = rect.bottom + scrollY + gap;
      }

      const maxTop = scrollY + padding;
      const minTop = scrollY + viewportHeight - estimatedMenuHeight - padding;
      top = Math.max(maxTop, Math.min(minTop, top));

      let right = viewportWidth - rect.right - scrollX;
      if (right < menuWidth + padding && spaceLeft > spaceRight) {
        right = viewportWidth - rect.left - scrollX;
      }
      right = Math.max(padding, Math.min(right, viewportWidth - menuWidth - padding));

      setPosition({ 
        top, 
        right, 
        openUpward: shouldOpenUpward 
      });
    }
  };

  // Handle click outside to close menu
  useEffect(() => {
    if (!isOpen) return;

    const handleClickOutside = (event: MouseEvent | TouchEvent) => {
      const target = event.target as Node;
      if (
        menuRef.current &&
        !menuRef.current.contains(target) &&
        buttonRef.current &&
        !buttonRef.current.contains(target)
      ) {
        setIsOpen(false);
      }
    };

    const handleEscape = (event: KeyboardEvent) => {
      if (event.key === 'Escape') {
        setIsOpen(false);
      }
    };

    // Use a small delay before adding click outside listener to avoid immediate closing
    const timeoutId = setTimeout(() => {
      document.addEventListener('click', handleClickOutside, true);
      document.addEventListener('touchstart', handleClickOutside, true);
    }, 10);

    document.addEventListener('keydown', handleEscape);

    // Update position after menu is rendered
    const positionTimeoutId = setTimeout(() => {
      updatePosition();
    }, 0);

    // Also update on scroll and resize
    const handleScroll = () => {
      updatePosition();
    };
    const handleResize = () => {
      updatePosition();
    };

    window.addEventListener('scroll', handleScroll, true);
    window.addEventListener('resize', handleResize);

    return () => {
      clearTimeout(timeoutId);
      clearTimeout(positionTimeoutId);
      document.removeEventListener('click', handleClickOutside, true);
      document.removeEventListener('touchstart', handleClickOutside, true);
      document.removeEventListener('keydown', handleEscape);
      window.removeEventListener('scroll', handleScroll, true);
      window.removeEventListener('resize', handleResize);
    };
  }, [isOpen, items.length]);


  const handleToggle = (e: React.MouseEvent) => {
    e.stopPropagation();
    e.preventDefault();
    if (!disabled) {
      setIsOpen((prev) => !prev);
    }
  };

  const handleItemClick = (item: DropdownMenuItem) => {
    if (!item.disabled) {
      item.onClick();
      setIsOpen(false);
    }
  };

  return (
    <div className="relative">
      {/* Three dots button */}
      <button
        ref={buttonRef}
        onClick={handleToggle}
        disabled={disabled}
        className={`
          p-2 rounded-lg transition-all duration-200
          ${disabled 
            ? 'opacity-50 cursor-not-allowed' 
            : 'hover:bg-gray-100 active:bg-gray-200'
          }
          ${className}
        `}
        style={{ minWidth: '44px', minHeight: '44px' }} // Mobile-friendly tap target
        aria-label="More actions"
        aria-expanded={isOpen}
        aria-haspopup="menu"
      >
        <MoreVertical className="h-4 w-4 text-gray-600" />
      </button>

      {/* Dropdown menu - rendered in portal to avoid clipping */}
      {isOpen && typeof document !== 'undefined' && createPortal(
        <div
          ref={menuRef}
          className={`fixed z-[9999] bg-white border border-gray-200 rounded-lg shadow-lg py-1 min-w-[180px] max-w-[240px] ${
            isMobile ? 'w-[calc(100vw-32px)] max-w-[280px]' : ''
          }`}
          style={{
            top: position.top > 0 ? `${position.top}px` : 'auto',
            right: position.right > 0 ? `${position.right}px` : 'auto',
            bottom: position.top <= 0 ? '8px' : 'auto',
            maxHeight: '60vh',
            overflowY: 'auto',
            // Add smooth animation
            animation: 'fadeIn 0.15s ease-out',
          }}
          role="menu"
          aria-orientation="vertical"
          onClick={(e) => e.stopPropagation()}
          onMouseDown={(e) => e.stopPropagation()}
          onTouchStart={(e) => e.stopPropagation()}
        >
          {items.map((item) => (
            <button
              key={item.id}
              onClick={() => handleItemClick(item)}
              disabled={item.disabled}
              className={`
                w-full flex items-center px-4 py-3 text-sm transition-colors
                ${item.disabled
                  ? 'opacity-50 cursor-not-allowed text-gray-400'
                  : item.destructive
                  ? 'text-red-600 hover:bg-red-50 active:bg-red-100'
                  : 'text-gray-700 hover:bg-gray-50 active:bg-gray-100'
                }
              `}
              style={{ minHeight: '44px' }} // Mobile-friendly tap target
              role="menuitem"
            >
              <div className="mr-3 flex-shrink-0">
                {item.icon}
              </div>
              <span className="text-left flex-1">{item.label}</span>
            </button>
          ))}
        </div>,
        document.body
      )}
    </div>
  );
};

// Predefined action items for common use cases
export const createEditAction = (onEdit: () => void): DropdownMenuItem => ({
  id: 'edit',
  label: 'Edit',
  icon: <Edit2 className="h-4 w-4" />,
  onClick: onEdit,
});

export const createDeleteAction = (onDelete: () => void): DropdownMenuItem => ({
  id: 'delete',
  label: 'Delete',
  icon: <Trash2 className="h-4 w-4" />,
  onClick: onDelete,
  destructive: true,
});

export const createAddChildAction = (onAddChild: () => void, type: 'folder' | 'card' = 'card'): DropdownMenuItem => ({
  id: 'add-child',
  label: `Add ${type === 'folder' ? 'Subfolder' : 'Card'}`,
  icon: type === 'folder' ? <Folder className="h-4 w-4" /> : <FileText className="h-4 w-4" />,
  onClick: onAddChild,
});

export const createMoveAction = (onMove: () => void): DropdownMenuItem => ({
  id: 'move',
  label: 'Move',
  icon: <Move className="h-4 w-4" />,
  onClick: onMove,
});

export const createDuplicateAction = (onDuplicate: () => void): DropdownMenuItem => ({
  id: 'duplicate',
  label: 'Duplicate',
  icon: <Copy className="h-4 w-4" />,
  onClick: onDuplicate,
});

export const createAICardAction = (onCreateAICard: () => void): DropdownMenuItem => ({
  id: 'ai-card',
  label: 'Add a card using AI',
  icon: <Sparkles className="h-4 w-4" />,
  onClick: onCreateAICard,
});

export const createPracticeCardAction = (onPractice: () => void): DropdownMenuItem => ({
  id: 'practice-card',
  label: 'Practice card',
  icon: <BookOpen className="h-4 w-4" />,
  onClick: onPractice,
});
