import { createContext, useContext, useState, useEffect, ReactNode, useCallback } from 'react';
import { User, AuthContextType } from '../types';
import { apiClient } from '../services/api';
import { AuthMeResponse } from '../types/api';

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const useAuth = (): AuthContextType => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

interface AuthProviderProps {
  children: ReactNode;
}

export const AuthProvider = ({ children }: AuthProviderProps) => {
  const [user, setUser] = useState<User | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Handle auth failure (when refresh fails)
  const handleAuthFailure = useCallback(() => {
    setUser(null);
    setIsAuthenticated(false);
  }, []);

  // Check authentication status on mount and when needed
  const checkAuth = useCallback(async () => {
    try {
      // Try to get current user info
      const userData = await apiClient.getMe();

      // User is authenticated
      setUser({
        id: userData.id,
        email: userData.email,
        created_at: new Date().toISOString(),
      });
      setIsAuthenticated(true);
      return true;
    } catch (error: any) {
      // If 401, try to refresh (this is expected when user is not logged in)
      if (error.response?.status === 401) {
        try {
          // Attempt to refresh the token
          await apiClient.refresh();

          // If refresh succeeds, try getting user info again
          const userData = await apiClient.getMe();
          setUser({
            id: userData.id,
            email: userData.email,
            created_at: new Date().toISOString(),
          });
          setIsAuthenticated(true);
          return true;
        } catch (refreshError: any) {
          // Refresh failed - user is not logged in (expected if no cookies exist)
          // Don't log 401 errors as they're expected when not logged in
          setUser(null);
          setIsAuthenticated(false);
          return false;
        }
      }

      // Other errors (network, etc.)
      setUser(null);
      setIsAuthenticated(false);
      return false;
    }
  }, []);

  // Check auth status on mount
  useEffect(() => {
    checkAuth().finally(() => {
      setIsLoading(false);
    });
  }, [checkAuth]);

  // Set auth failure callback on API client
  useEffect(() => {
    apiClient.setOnAuthFailure(handleAuthFailure);
  }, [handleAuthFailure]);

  const login = async (email: string, password: string): Promise<void> => {
    try {
      // Sign in - cookies are set by backend automatically
      await apiClient.signIn({ email, password });

      // Verify auth by calling /auth/me (backend requirement)
      // Wait a brief moment for cookies to be processed
      await new Promise(resolve => setTimeout(resolve, 100));

      // Get user info after successful login
      const userData = await apiClient.getMe();
      setUser({
        id: userData.id,
        email: userData.email,
        created_at: new Date().toISOString(),
      });
      setIsAuthenticated(true);
    } catch (error) {
      console.error('Login failed:', error);
      setUser(null);
      setIsAuthenticated(false);
      throw error;
    }
  };

  const logout = useCallback(async (): Promise<void> => {
    try {
      // Call logout endpoint to clear cookies on backend
      await apiClient.logout();
    } catch (error) {
      console.warn('Logout endpoint error:', error);
    } finally {
      // Clear local state regardless
      setUser(null);
      setIsAuthenticated(false);
    }
  }, []);

  const value: AuthContextType = {
    user,
    token: user !== null ? 'cookie' : null, // Keep token for compatibility, using cookie authentication
    login,
    logout,
    isLoading,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
