import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { apiClient } from '../services/api';
import { ReviewedCard } from '../types/api';
import { Loader2, BookOpen, Folder, Tag, Calendar, TrendingUp, ArrowRight } from 'lucide-react';

export const Dashboard: React.FC = () => {
  const [reviewedCards, setReviewedCards] = useState<ReviewedCard[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedDays, setSelectedDays] = useState<number>(7);
  const [total, setTotal] = useState<number>(0);

  useEffect(() => {
    loadReviewedCards();
  }, [selectedDays]);

  const loadReviewedCards = async () => {
    try {
      setIsLoading(true);
      setError(null);
      const response = await apiClient.getReviewedCards(selectedDays);
      setReviewedCards(response.items);
      setTotal(response.total);
    } catch (err: any) {
      console.error('Error loading reviewed cards:', err);
      setError(`Failed to load reviewed cards: ${err.message || err.response?.data?.detail || 'Unknown error'}`);
    } finally {
      setIsLoading(false);
    }
  };

  const formatDate = (dateString: string): string => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };


  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Dashboard</h1>
          <p className="text-sm text-gray-600 mt-1">Track your vocabulary learning progress</p>
        </div>
        <Link
          to="/tree"
          className="flex items-center space-x-2 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors"
        >
          <span>Go to Library</span>
          <ArrowRight className="h-4 w-4" />
        </Link>
      </div>

      {/* Last Reviewed Cards Section */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200">
        <div className="p-6 border-b border-gray-200">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="p-2 bg-blue-100 rounded-lg">
                <BookOpen className="h-5 w-5 text-blue-600" />
              </div>
              <div>
                <h2 className="text-lg font-semibold text-gray-900">Last Reviewed Cards</h2>
                <p className="text-sm text-gray-600">
                  {total} {total === 1 ? 'card' : 'cards'} reviewed
                </p>
              </div>
            </div>

            {/* Time Range Selector */}
            <div className="flex items-center space-x-2">
              <span className="text-sm text-gray-600">Time range:</span>
              <div className="flex items-center space-x-1 bg-gray-100 rounded-lg p-1">
                <button
                  onClick={() => setSelectedDays(7)}
                  className={`px-3 py-1 text-sm font-medium rounded-md transition-colors ${
                    selectedDays === 7
                      ? 'bg-white text-blue-600 shadow-sm'
                      : 'text-gray-600 hover:text-gray-900'
                  }`}
                >
                  7 days
                </button>
                <button
                  onClick={() => setSelectedDays(30)}
                  className={`px-3 py-1 text-sm font-medium rounded-md transition-colors ${
                    selectedDays === 30
                      ? 'bg-white text-blue-600 shadow-sm'
                      : 'text-gray-600 hover:text-gray-900'
                  }`}
                >
                  30 days
                </button>
                <button
                  onClick={() => setSelectedDays(90)}
                  className={`px-3 py-1 text-sm font-medium rounded-md transition-colors ${
                    selectedDays === 90
                      ? 'bg-white text-blue-600 shadow-sm'
                      : 'text-gray-600 hover:text-gray-900'
                  }`}
                >
                  90 days
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Cards List */}
        <div className="p-6">
          {isLoading ? (
            <div className="flex items-center justify-center py-12">
              <Loader2 className="h-6 w-6 animate-spin text-blue-600" />
              <span className="ml-2 text-gray-600">Loading reviewed cards...</span>
            </div>
          ) : error ? (
            <div className="rounded-md bg-red-50 p-4">
              <div className="text-sm text-red-700">{error}</div>
              <button
                onClick={loadReviewedCards}
                className="mt-2 text-sm text-red-600 hover:text-red-800 underline"
              >
                Try again
              </button>
            </div>
          ) : reviewedCards.length === 0 ? (
            <div className="flex flex-col items-center justify-center py-12 text-gray-500">
              <BookOpen className="h-12 w-12 mb-4 opacity-50" />
              <p className="text-lg font-medium mb-2">No reviewed cards</p>
              <p className="text-sm text-center mb-4">
                You haven't reviewed any cards in the last {selectedDays} days
              </p>
              <Link
                to="/tree"
                className="px-4 py-2 text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 rounded-md"
              >
                Go to Library
              </Link>
            </div>
          ) : (
            <div className="space-y-4">
              {reviewedCards.map((card) => (
                <div
                  key={card.id}
                  className="border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow"
                >
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      {/* Card Name */}
                      <h3 className="text-lg font-semibold text-gray-900 mb-3">{card.name}</h3>

                      {/* Category and Folder Info */}
                      <div className="flex flex-wrap items-center gap-4 mb-3">
                        {card.category_name && (
                          <div className="flex items-center space-x-2 text-sm text-gray-600">
                            <Tag className="h-4 w-4 text-purple-600" />
                            <span className="font-medium text-gray-900">{card.category_name}</span>
                          </div>
                        )}
                        {card.parent_name && (
                          <div className="flex items-center space-x-2 text-sm text-gray-600">
                            <Folder className="h-4 w-4 text-blue-600" />
                            <span>{card.parent_name}</span>
                          </div>
                        )}
                      </div>

                      {/* Review Stats */}
                      <div className="flex flex-wrap items-center gap-4 text-sm">
                        <div className="flex items-center space-x-2 text-gray-600">
                          <Calendar className="h-4 w-4" />
                          <span>
                            Last reviewed: <span className="font-medium text-gray-900">{formatDate(card.last_reviewed_at)}</span>
                          </span>
                        </div>
                        {card.next_review_at && (
                          <div className="flex items-center space-x-2 text-gray-600">
                            <TrendingUp className="h-4 w-4" />
                            <span>
                              Next review: <span className="font-medium text-gray-900">{formatDate(card.next_review_at)}</span>
                            </span>
                          </div>
                        )}
                        <div className="flex items-center space-x-2">
                          <span className="text-gray-600">Reviews:</span>
                          <span className="font-medium text-gray-900">{card.review_count}</span>
                        </div>
                      </div>
                    </div>

                    {/* Mastery Level Badge */}
                    <div className="ml-4">
                      <span
                        className={`px-3 py-1 text-xs font-semibold rounded-full ${getMasteryLevelColor(card.mastery_level)}`}
                      >
                        {getMasteryLevelLabel(card.mastery_level)}
                      </span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};
